INSERT INTO `xlite_notification_translations` VALUES (1,'failed_admin_login','Failed admin login','The notification is sent to the email address of the admin user for whose account failed login attempts have been detected several times in a row. A copy of the message is also sent to the store’s super admin user.','','','WARNING! Administrator login failure','Failed administrator login detected.','en'),(2,'low_limit_warning','Low limit warning','This message is used to notify the administrator when the stock level of a product reaches the low stock limit as specified in the product details.','','','Low stock warning','The inventory level of a product in your store has reached the low limit.','en'),(3,'order_advanced_changed','Order changed with advanced order management','This message is sent to notify the customer of any changes made to their order by an administrator using the advanced order management feature.','Your order has been changed','Your order has been changed by customer service. Here are the details of your order:','','','en'),(4,'order_canceled','Order canceled','This notification is sent to both the administrator and customer when an order gets the status Canceled.','Your order has been canceled','Your order has been canceled.','Order has been canceled','Order has been canceled.','en'),(5,'order_changed','Order changed','This notification is sent to both the administrator and customer when the status of an order is updated - in the event that no other type of notification is sent.','Your order has been changed','Your order has been changed. Here are the details of your order:','Order has been changed','Order has been changed.','en'),(6,'order_created','Order created','This notification is sent to both the administrator and customer when a new order is created (Disabled by default).','Order has been created','Thank you for shopping with us! We received your order.<br />You will receive an order confirmation once your order is processed.','Order has been created','Order has been created.','en'),(7,'order_failed','Order declined','This notification is sent to both the administrator and customer when an order gets the status Declined.','Your order has been declined','The status of your order has been changed to \"Declined\". This could be for one of the following reasons:<br />1) The order was declined by the store administrator.<br />2) The payment could not be processed at the time of order placement.<br />For more information, please contact the store administrator.','Order has been declined','Order has been declined.','en'),(8,'order_processed','Order processed','This notification is sent to both the administrator and customer when an order gets the status Paid.','Order confirmation','Your order has been processed. Thank you for your business.<br />We will notify you by email of any further order status updates.','Order has been processed','Order has been processed.','en'),(9,'order_shipped','Order shipped','This notification is sent to the customer when an order gets the status Shipped.','Your order has been shipped','Your order has been shipped. Thank you for your business.<br />Please come back soon!','','','en'),(10,'order_waiting_for_approve','Order awaiting for approval','This notification is sent to the customer when an order gets the status Waiting for approve.','Order awaiting for approval','Thank you for your purchase. Our team will provide you with the download links in a couple of hours. Sorry for the inconveniences.','','','en'),(11,'order_tracking_information','Order tracking information','This notification is sent to the customer when an administrator clicks the \"Send tracking information to customer\" button.','Your order tracking information','Here are the tracking details for your order','','','en'),(12,'profile_created','User profile created','This notification is sent to both the administrator and customer when a new customer profile is created.','Welcome!','Thank you for registering with us.','New user registration','A new user profile has been registered:','en'),(13,'profile_deleted','User profile deleted','This notification is sent to the administrator when a customer profile is deleted.','','','User profile has been deleted','The following user profile has been deleted:','en'),(14,'profile_modified','User profile modified','This notification is sent to both the administrator and customer when a user profile is updated.','Your profile has been modified','Your profile has been modified. To view your profile information, log in at our site.','User profile has been modified','The user profile has been modified.','en'),(15,'recover_password_request','Password reset request','This notification is sent to the user when a password reset is requested for their account.','Confirm password reset request','<p>You have received this message because you requested to reset your forgotten password.<br />If you did not submit such a request, it might mean that somebody was trying to gain access to your account.</p><p>To confirm that this request was submitted by you, click on the link below<br/>(If the link does not work, please copy and paste the entire URL into your browser\'s address bar and press Enter).</p>','Confirm password reset request','<p>You have received this message because you requested to reset your forgotten password.<br />If you did not submit such a request, it might mean that somebody was trying to gain access to your account.</p><p>To confirm that this request was submitted by you, click on the link below<br/>(If the link does not work, please copy and paste the entire URL into your browser\'s address bar and press Enter).</p>','en'),(16,'register_anonymous','User account created for guest buyer based on order info','This notification is sent to the customer when a customer profile is created for them by the store administrator based on a previously created order.','User account created','<p>Thank you for shopping with us! We noticed that one or more orders you made at our store were placed using guest checkout. We appreciate your business and look forward to continue serving you in the future. For your convenience, we created an account for you at our store. The account stores all the information about your previous orders with us.</p><p>To log in to your account, please use the following credentials:<br />(The password can be changed in your account profile.)</p>','','','en'),(17,'failed_transaction','Failed transaction','This notification is sent to the administrator if payment transaction was failed','','','Failed transaction','','en'),(18,'modules/CDev/ContactUs/message','Contact us form submitted (Contact us module)','This notification is sent to the administrator when someone submits a request via the \"Contact us\" form.','','','Someone has used the Contact us form','','en'),(19,'modules/CDev/ContactUs/message','Заполнена форма для связи с администрацией магазина (модуль Contact us)','Данное сообщение используется для оповещения администратора в случае поступления в адрес магазина обращения через форму для связи с администрацией.','','','Поступило обращение через форму для связи с администрацией магазина','','ru'),(20,'modules/CDev/Egoods/egoods_links','E-goods download links (E-goods module)','This notification is sent to the customer when their order containing egoods gets the status Paid.','Your download links','Your order has been processed. The files for the products you purchased can be downloaded at:','','','en'),(21,'modules/CDev/Egoods/egoods_links','Ссылки для скачивания файлов (модуль E-goods)','Данное сообщение отправляется покупателю, когда размещенный им заказ, содержащий электронные продукты, получает статус \"Оплачен\".','Ваши ссылки для скачивания файлов','Благодарим Вас за своевременную оплату заказа. Файлы приобретенных Вами товаров могут быть загружены по следующим ссылкам:','','','ru'),(22,'modules/CDev/PINCodes/acquire_pin_codes_failed','PIN code generation failure (PIN codes module)','This notification is sent to the administrator in the event of an error in the process of generating PIN codes for one or more items - as a result of which the PIN codes have not been generated.','','','PIN code generation failure for one or more ordered items','Could not generate PIN codes for the following products:','en'),(23,'modules/CDev/PINCodes/acquire_pin_codes_failed','Ошибка создания пин-кода (модуль PIN codes)','Данное сообщение отправляется администратору в случае, когда при создании PIN-кодов для одного или нескольких товаров произошла ошибка, в результате которой PIN-код(ы) не были созданы.','','','Ошибка создания PIN-кода для одного или нескольких заказанных продуктов','Не удалось создать PIN-коды для следующих продуктов:','ru'),(24,'modules/XC/Reviews/new_review','New product review submitted - (Product Reviews module)','This message is used to notify the administrator when a customer submits a new product review.','','','A new review has been submitted','','en'),(25,'modules/XC/Reviews/new_review','Получен новый отзыв о товаре - (модуль Product Reviews)','Данное сообщение используется для уведомления администратора о том, что клиент оставил отзыв о каком-либо товаре.','','','Получен новый отзыв о товаре','','ru'),(26,'modules/XC/Reviews/review_key','Request to review purchased products - (Product Reviews module)','This message is used to invite customers to leave feedback on the products they have purchased.','Please review your purchase','','','','en'),(27,'modules/XC/Reviews/review_key','Приглашение оставить отзыв на купленные товары - (модуль Product Reviews)','Данное сообщение используется для отправки покупателю приглашения оставить отзыв о купленных им товарах.','Пожалуйста, оставьте отзыв о купленных товарах','','','','ru'),(28,'modules/XC/VendorMessages/notification','Order message (Order messages module)','This message will be sent to customer when a new message appears in the communication thread regarding an order','New message for order %order_number%','%company_link%\n<p>\nYou\'ve got a message regarding your order %order_messages_link%:\n</p>\n<p>\n%message%\n</p>','','','en'),(29,'modules/XC/VendorMessages/new_message_notification','New message (Order messages module)','This message will be sent to customer when a new message appears in the conversation','New message in conversation','%company_link%\n<p>\nYou\'ve got a new message in %conversation_link%:\n</p>\n<p>\n%message%\n</p>\n','','','en'),(30,'modules/XC/VendorMessages/notification','Новое сообщение (модуль Order messages)','Это уведомление будет отправлено покупателю, когда в разговоре появится новое сообщение','Новое сообщение в разговоре','%company_link%\n<p>\nВы получили новое сообщение в %conversation_link%:\n</p>\n<p>\n%message%\n</p>','','','ru'),(31,'modules/XC/CanadaPost/return_approved','Return request approved (Canada Post module)','This notification is sent to the customer if their request to return a product via Canada Post is approved by the administrator.','Your return request has been approved','Your return request has been approved','','','en'),(32,'modules/XC/CanadaPost/return_approved','Запрос на возврат товара одобрен (модуль Canada Post)','Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post получает одобрение администратора.','Ваш запрос на возврат товара одобрен','Ваш запрос на возврат товара одобрен','','','ru'),(33,'modules/XC/CanadaPost/return_rejected','Return request rejected (Canada Post module)','This notification is sent to the customer if their request to return a product via Canada Post is rejected by the administrator.','Your return request has been rejected','Your return request has been rejected','','','en'),(34,'modules/XC/CanadaPost/return_rejected','Запрос на возврат товара отклонен (модуль Canada Post)','Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post отклоняется администратором.','Ваш запрос на возврат товара отклонен','Ваш запрос на возврат товара отклонен','','','ru');
